package week1.day1.test;

public class LearnOperators {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		
		int i =12;
		int j = 2;
		
		System.out.println(i+j);
		
		
		System.out.println(i-j);
		
		System.out.println(i%j);
		
		System.out.println(i/j);
		
		System.out.println(++i);
		System.out.println(j);
		

	}

}
